import React, { useState } from "react";
import { VscClose } from "react-icons/vsc";
import TextareaAutosize from 'react-textarea-autosize';
import "../styles/modal.css";
import API from "./api";

function Modal(props) {
    const [description, setDescription] = useState(props.description);
    const [taskid] = useState(props.taskid);
    const [dynamisation, setDynamisation] = useState(false);
    const [detailid, setDetailID] = useState();

    const saveDescripion = (path, data) => {
        API.put(path, {
            data
        })
        .then(response => {
            console.log(response);
        })
        .catch(err => {
            // what should i do
            console.log(err);
        })
    }   

    const textArea =
        <TextareaAutosize 
            className="text-area-for-descripton"
            onChange={(e) => setDescription(e.target.value)}
            autoFocus={true}
            placeholder={"description..."} 
        />

    const detail =
            <p className="card-text" onClick={() => setDynamisation(true)}>{description}</p>

        return (
        <div id="overlay">
            <div id="content">
                <div className="card">
                    <div className="card-body">
                        <div className="d-flex justify-content-between">
                            <h4 className="card-title">{props.taskName}</h4>
                            <div className="close" onClick={() => props.setOpen(false)}>
                                <VscClose />
                            </div>  
                        </div>
                        <h5 className="description">description</h5>
                        <div >
                            {dynamisation ? textArea : detail}
                        </div>
                        <button className="btn btn-primary" onClick={() => saveDescripion("detail", {description : description, taskid: taskid})}>Save</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Modal;
