import React, { useEffect, useState } from "react";
import TaskSection from "../components/task-section";
import EnterArea from "../components/enterArea";
import AddButton from "../components/addButton";
import axios from 'axios';
import postData from "../api/post";
import 'bootstrap/dist/css/bootstrap.min.css';
import "../styles/board.css";
import { BsPlusCircleDotted } from "react-icons/bs";
axios.defaults.withCredentials = true;
const api = process.env.REACT_APP_API;

function Board(props) {
    const [tasklist, setTasklist] = useState(props.tasklist);
    const [listName, setListName] = useState("");
    const [dynamisation, setDynamisation] = useState(false);

    useEffect(() => {
        axios.get(api + "/board")
        .then(response => {
            const data = response.data
            setTasklist(data);
        })
        .catch(err => {

        })
    }, [])

    const addTaskList = () => {
        setDynamisation(true);
    }

    const cancelAdd = (e) => {
        e.preventDefault()
        setDynamisation(false)
    }

    const handleChange = (e) => {
        setListName(e.target.value);
    }

    const handleSubmit = (e, path, data) => {
        e.preventDefault();
        // if (listName.length === 0) return ;
        const keys = Object.keys(data);
        if (data[keys[0]].length === 0) return ;
        console.log(data);
        // const data = {
        //     listName: listName,
        // }
        postData(path, data)
        // axios.post(api + path, {
        //     headers: {'Content-Type': 'application/json'},
        //     mode: 'cors',
        //     data
        // })
        .then(response => {
            const  { tasklistID, listName, Tasks } = response.data; 
            const newList = { id: tasklistID, listName: listName, Tasks: Tasks };
            setTasklist([...tasklist, newList])
            setDynamisation(false)
            setListName("")
        })
        .catch(err => {

        })
    }

    const deletelist = (id) => {
        const data = {
            listid: id,
        }
        axios.delete(api + '/tasklist', {
            headers: {'Content-Type': 'application/json'},
            mode: 'cors',
            data
        })
        .then(response => {

        })
        .catch((response, err) => {

        })

        const remainlist = tasklist.filter(list => id !== list.id);
        setTasklist(remainlist)
    }

    const enterArea = 
        <EnterArea 
            handleSubmit={(e) => handleSubmit(e, "/tasklist", { listName: listName })}
            wrap="enter-section-name-wrap"
            handleChange={handleChange}
            text="Add List"
            placeholder="Enter list title..."
            cancel={cancelAdd}
        />

    const addAnotherListBtn = 
        <AddButton 
            addElement={addTaskList}
            text="Add another list"
            ClassName="add-list"    
        />

    const sectionList = tasklist
    .map(section => (
        <TaskSection
            id={section.id}
            key={section.id}
            name={cuttingStr(section.listName)}
            deletelist={deletelist}
            task={section.Tasks}
        />
    ))

    return (
            <div className="board-wrapper">
                <div className="board-canvas">
                    {sectionList}
                    <div className="mx-1">
                        {dynamisation ? enterArea : addAnotherListBtn}
                    </div>
                </div>
            </div>
        )
}

function cuttingStr(str) {
    const bytes = encodeURI(str).replace(/%../g, "*").length;
    return bytes >= 24 ? str.substr(0, 23) + "..." : str;
}

export default Board;
