import axios from "axios";
const api = process.env.REACT_APP_API;
axios.defaults.withCredentials = true;

function postData(path, data) {
    axios.post(api + path, {
        headers: {'Content-Type': 'application/json'},
        mode: 'cors',
        data
    })
    .then(res => {
        return res;
    })
    .catch(err => {
        return err;
    })
}

export default postData;
