import React, { useState } from "react";
import TextareaAutosize from 'react-textarea-autosize';
import { BsTrash } from 'react-icons/bs';
import API from "./api";
import cuttingStr from "./shaping";
import Modal from "./modal";
import "../styles/modal.css"

function TaskCard(props) {
    const [editMode, setEditMode] = useState(false);
    const [taskName, setTaskName] = useState(props.name);
    const [taskID] = useState(props.id);

    const keyPress = (e, path, data) => {
        if (e.which === 13) {
            setEditMode(false)
            API.put(path, {
                data
            })
            .then(response => {
                setTaskName(cuttingStr(response.data.TaskName));
            })
            .catch(err => {
                // what should I do 
                window.location.reload();
            })
        }
    }

    // const editDetail = 
    //     <TextareaAutosize 
    //         className="task-detail" 
    //         defaultValue=""
    //         onKeyPress={(e) => keyPress(e)} 
    //         disabled={editMode}
    //         onClick={() => setEditMode(false)}
    //     />

    const task =  
            <>
                <p className="task-name" onClick={() => setEditMode(true)}>{taskName}</p>
            </>

    const textArea =
                <TextareaAutosize 
                    className="task-name"
                    onKeyPress={(e) => keyPress(e, "task", {id: taskID, TaskName: taskName})} 
                    onChange={(e) => setTaskName(e.target.value)}
                    autoFocus={true}
                />

    return (
            <div className="toast fade show" role="alert" aria-live="assertive" aria-atomic="true">
                <div className="toast-header">
                    {editMode ? textArea : task}
                    <div className="trash-box-icon" onClick={() => props.deleteTask("task", { id: props.id }, props.id)}>
                        <BsTrash />
                    </div>
                </div>
                {/* <div className="toast-body">
                    <small style={{color: "gray"}}>detail</small>
                    {editDetail}
                </div> */}
                <Modal />
            </div>
        )
}


export default TaskCard;
