import React from "react";
import GoogleLogin from "react-google-login";
import axios from 'axios';
import "../styles/login.css";

const googleClientId = process.env.REACT_APP_GOOGLE_CLIENT_ID;
const api = process.env.REACT_APP_API;

function Login(props) {

    const handleLoginWithGoogle = (response) => {
        const data = {
            token: response.tokenId
        }
        axios.post(api + "/auth/google", {
            headers: {'Content-Type': 'application/json'},
            mode: 'cors',
            data
        })
        .then(response => {
            const { data } = response;
            props.setCookie("indiv", data.id, {maxAge: 3600}, {httpOnly: true});
            props.setCookie("authenticated", data.auth, {maxAge: 3600}, {secure: true})
            props.authenticate(true);
        })
        .catch(err => {
            props.authenticate(false);
        })
    }

    const failureLoginWithGoogle = (response) => {
        props.authenticate(false)
    } 

    return (
        <>
            <div className="d-flex justify-content-center align-items-center vh-100">
                <div className="col-10 col-md-8 col-lg-4 login-container">
                    <div className="container py-4">
                        <div className="d-flex justify-content-center">
                            <h2 className="rem1 signin">Sign in</h2>
                        </div>
                        <div className="d-flex justify-content-around pt-3">
                            <div className="login-with-google">
                                <GoogleLogin
                                    clientId={googleClientId}
                                    buttonText="Sign in with Google"
                                    onSuccess={handleLoginWithGoogle}
                                    onFailure={failureLoginWithGoogle}
                                    cookiePolicy={'single_host_origin'}
                                />
                            </div>
                        </div>
                    </div>
                </div>        
            </div>
        </>
    )
}

export default Login;
