import React, { useState, useLayoutEffect } from "react";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Redirect
} from "react-router-dom";
import Board from "./pages/board";
import Login from "./pages/login";
import NotFoundPage from "./pages/notfound";
import { useCookies } from "react-cookie";
import axios from "axios";
axios.defaults.withCredentials = true;
const api = process.env.REACT_APP_API;


function App(props) {
    const [cookies] = useCookies();
    const [loggedIn, setAuthenticated] = useState(cookies.authenticated === undefined ? false : true);

    useLayoutEffect(() => {
        axios.get(api)
    }, [])

    return (
        <>
            <Router>
                <Switch>
                    <Route exact path="/board">
                        {/* {loggedIn ? <Board tasklist={props.tasklist}/> : <Redirect to="/" />} */}
                        <Board tasklist={props.tasklist}/>
                    </Route>
                    <Route exact path="/">
                        {/* {loggedIn ? <Redirect to="/board" /> : <Login loggedIn={loggedIn} authenticate={setAuthenticated}/>} */}
                        <Login loggedIn={loggedIn} authenticate={setAuthenticated}/>
                    </Route>
                    <Route>
                        <NotFoundPage />
                    </Route>
                </Switch>
            </Router>
        </>
        )
}


export default App;
