import React from "react";
import { Container, Row, Col } from "react-bootstrap";

function TopPage() {
	return (
		<>
			<Container> 
	      <Row>
  			  <Col>
						<div className="vh-100 d-flex flex-column align-items-center justify-content-center">
							<h1>task management app</h1>
							<p>trelloをモチーフにしたカンバン方式のタスク管理Web Applicationです。</p>
							<p>登録したタスクはDataBaseへ保存されますが、ログイン後に表示されるDeleteボタンをクリックするとユーザー情報からあなたが登録したタスクの全てをDataBaseから削除できます。</p>
	 					</div>
					</Col>
   			  <Col>2 of 2

					</Col>
 				 </Row>
			</Container>
		</>

	)
}


export default TopPage;
