import React, { useState } from "react";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Redirect
} from "react-router-dom";
import Board from "./components/board";
import Login from "./components/login";
import NotFoundPage from "./components/notfound";
import { useCookies } from "react-cookie";
import axios from "axios";

const instance = axios.create({
    baseURL: process.env.REACT_APP_API,
    headers: {'Content-Type': 'application/json'},
    mode: 'cors',
});

function App(props) {
    const [cookies] = useCookies();
    const [loggedIn, setAuthenticated] = useState(cookies.authenticated === undefined ? false : true);
    const [token, setToken] = useState(token === undefined ? null : token);

    return (
        <>
            <Router>
                <Switch>
                    <Route exact path="/board">
                        {loggedIn ? <Board tasklist={props.tasklist} axios={instance} token={token}/> : <Redirect to="/" />}
                    </Route>
                    <Route exact path="/">
                        {loggedIn ? <Redirect to="/board" /> : <Login loggedIn={loggedIn} authenticate={setAuthenticated} token={setToken}/>}
                    </Route>
                    <Route>
                        <NotFoundPage />
                    </Route>
                </Switch>
            </Router>
        </>
        )
}


export default App;
