import React, { useEffect, useState } from "react";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Redirect
} from "react-router-dom";
import Board from "./pages/board";
import Login from "./pages/login";
import NotFoundPage from "./pages/notfound";
import { useCookies } from "react-cookie";
import axios from "axios";
const api = process.env.REACT_APP_API;
axios.defaults.withCredentials = true;

function App(props) {
    const [cookies, setCookie] = useCookies();
    const [loggedIn, setAuthenticated] = useState();

    console.log(document.cookie);

    return (
        <>
            <Router>
                <Switch>
                    <Route exact path="/board">
                        {/* {loggedIn ? <Board tasklist={props.tasklist}/> : <Redirect to="/" />} */}
                        <Board tasklist={props.tasklist} />
                    </Route>
                    <Route exact path="/">
                        {loggedIn ? <Redirect to="/board" /> : 
                        <Login loggedIn={loggedIn} authenticate={setAuthenticated} setCookie={setCookie} />}
                    </Route>
                    <Route>
                        <NotFoundPage />
                    </Route>
                </Switch>
            </Router>
        </>
        )
}

function isAu() {
    if (document.cookie.split(';').some((item) => item.trim().startsWith('reader='))) {
        return true;
    }
    else  {
        return false;
    }
}

export default App;
