import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import Login from "./login"

function TopPage(props) {
	return (
		<>
			<Container> 
				<Row>
					<Col>
						<div className="vh-100 d-flex flex-column align-items-center ">
							<h1>task management app</h1>
							{/* <p>trelloをモチーフにしたカンバン方式のタスク管理Web Applicationです。</p>
							<p>登録したタスクはDataBaseへ保存されますが、ログイン後に表示されるDeleteボタンをクリックするとあなたが登録したユーザー、タスクに関するデータ全てをDataBaseから削除できます。</p> */}
						</div>
					</Col>
					<Col>
						<Login loggedIn={props.loggedIn} authenticate={props.authenticate} />
					</Col>
				</Row>
			</Container>
		</>

	)
}


export default TopPage;
