import React, { useState } from "react";
// import GoogleLogin from "react-google-login";
import "../styles/login.css";
import API from "./api";
import { Form, Button } from "react-bootstrap";

const env = process.NODE_ENV;
// const googleClientId = env === "production" ?  process.env.REACT_APP_GOOGLE_CLIENT_ID : process.env.REACT_APP_GOOGLE_CLIENT_ID_DEV;

function Login(props) {
    const [username, setUserName] = useState("");
    const [password, setPassword] = useState("");

    // const handleLoginWithGoogle = (response) => {
    //     const data = { token: response.tokenId };
    //     API.post("auth/google", {
    //         data
    //     })
    //     .then(response => {
    //         props.authenticate(true);
    //     })
    //     .catch(err => {
    //         console.log(err);
    //         props.authenticate(false);
    //     })
    // }

    // const failureLoginWithGoogle = (response) => {
    //     console.log(response);
    //     props.authenticate(false)
    // }

    const handleChange = (e) => {
        switch (e.target.id) {
            case "username":
                setUserName(e.target.value);
                break
            case "password":
                setPassword(e.target.value);
                break;        
            default:
                break;
        }
    }

    const formSubmit = (e, path) => {
        e.preventDefault();
        if (username.length < 8 || password.length < 8) return;


        const data = {
            username: username,
            password: password
        }

        if (e.target.id === "signup") {
            API.post("/signup", {
                data
            })
            .then(res => {
                props.authenticate(true);
            })
            .catch(err => {
                props.authenticate(false);
            })
        }


    }

    return (
        <>
            <div className="d-flex justify-content-center align-items-center vh-100">
                <div className="login-container">
                    <div className="container py-4">
                        <div className="d-flex justify-content-center">
                            <h2 className="rem1">Welcome to My app</h2>
                        </div>
                        <Form>
                            <Form.Text>user name and password must be at least 8 characters</Form.Text>
                            <Form.Group className="mb-3">
                                <Form.Label>User Name</Form.Label>
                                <div>
                                    <Form.Text>{}</Form.Text>
                                </div>
                                <Form.Control 
                                    type="text" 
                                    placeholder="Username" 
                                    minLength="1" 
                                    maxLength="255"
                                    onChange={(e) => handleChange(e)}
                                    id="username"
                                />
                            </Form.Group>
                            <Form.Group className="mb-3">
                                <Form.Label>Password</Form.Label>
                                <Form.Control
                                    minLength="8" 
                                    maxLength="255" 
                                    type="password" 
                                    placeholder="Password"
                                    onChange={(e) => handleChange(e)}
                                    id="password"
                                />
                            </Form.Group>
                                <div className="d-flex">
                                <Button variant="outline-secondary" id="signup" onClick={(e) => formSubmit(e, "signup")}>
                                    Sign Up
                                </Button>
                                <Button className="mx-2 px-3" variant="outline-secondary" id="login" onClick={(e) => formSubmit(e, "login")}>
                                    Login
                                </Button>
                                </div>
                        </Form>                        
                        {/* <div className="d-flex justify-content-around pt-3">
                            <div className="login-with-google">
                                <GoogleLogin
                                    clientId={googleClientId}
                                    buttonText="Sign in with Google"
                                    onSuccess={handleLoginWithGoogle}
                                    onFailure={failureLoginWithGoogle}
                                    cookiePolicy={'single_host_origin'}
                                />
                            </div>
                        </div> */}
                    </div>
                </div>        
            </div>
        </>
    )
}

function validationChar(char) {
    let charCode = char.charCodeAt(0);
    console.log(`${char} : ${charCode}`);
    if (charCode === 34 || 
        charCode === 39 || 
        charCode === 38 || 
        charCode === 60 || 
        charCode === 62
    ) return false;

    else if (charCode < 33 ||
            127 <= charCode
    ) return false;

    else return true;

}

export default Login;
