import React, { useState } from "react";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Redirect
} from "react-router-dom";
import Board from "./components/board";
import Login from "./components/login";
import NotFoundPage from "./components/notfound";
import { useCookies } from "react-cookie";


function App(props) {
    const [cookies] = useCookies();
    const [loggedIn, setAuthenticated] = useState(cookies.authenticated === undefined ? false : true)

    return (
        <>
            <Router>
                <Switch>
                    <Route exact path="/board">
                        {loggedIn ? <Board tasklist={props.tasklist} /> : <Redirect to="/" />}
                    </Route>
                    <Route exact path="/">
                        {loggedIn ? <Redirect to="/board" /> : <Login loggedIn={loggedIn} authenticate={setAuthenticated} />}
                    </Route>
                    <Route>
                        <NotFoundPage />
                    </Route>
                </Switch>
            </Router>
        </>
        )
}


export default App;
