import React, { useState } from "react";
// import GoogleLogin from "react-google-login";
import "../styles/login.css";
import API from "./api";
import { Form, Button } from "react-bootstrap";

const env = process.NODE_ENV;
// const googleClientId = env === "production" ?  process.env.REACT_APP_GOOGLE_CLIENT_ID : process.env.REACT_APP_GOOGLE_CLIENT_ID_DEV;

function Login(props) {
	const [userName, setUserName] = useState("");
    const [password, setPassword] = useState("");
    const [alert, setAlert] = useState(false);

    // const handleLoginWithGoogle = (response) => {
    //     const data = { token: response.tokenId };
    //     API.post("auth/google", {
    //         data
    //     })
    //     .then(response => {
    //         props.authenticate(true);
    //     })
    //     .catch(err => {
    //         console.log(err);
    //         props.authenticate(false);
    //     })
    // }

    // const failureLoginWithGoogle = (response) => {
    //     console.log(response);
    //     props.authenticate(false)
    // }

    const handleChangeUserName = (e) => {
        if (!validationChar(e.target.value)) {
            setAlert(true)
        }
    }

    return (
        <>
            <div className="d-flex justify-content-center align-items-center vh-100">
                <div className="login-container">
                    <div className="container py-4">
                        <div className="d-flex justify-content-center">
                            <h2 className="rem1">Welcome to My app</h2>
                        </div>
                        <Form>
                            <Form.Group className="mb-3" controlId="formBasicEmail">
                                <Form.Label>User Name</Form.Label>
                                <Form.Control 
                                    type="text" 
                                    placeholder="Username" 
                                    minLength="1" 
                                    maxLength="255"
                                />
                                <Form.Text className="text-muted">
                                    {"", "&", "<", ">", "は"}
                                </Form.Text>
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formBasicPassword">
                                <Form.Label>Password</Form.Label>
                                <Form.Control 
                                    minLength="8" 
                                    maxLength="255" 
                                    type="password" 
                                    placeholder="Password"
                                    onChange={handleChangeUserName}
                                />
                            </Form.Group>
                            <div className="d-flex">
                                <Button variant="outline-secondary" type="submit">
                                    Sign Up
                                </Button>
                                <Button className="mx-2 px-3" variant="outline-secondary" type="submit">
                                    Login
                                </Button>
                            </div>
                        </Form>                        
                        {/* <div className="d-flex justify-content-around pt-3">
                            <div className="login-with-google">
                                <GoogleLogin
                                    clientId={googleClientId}
                                    buttonText="Sign in with Google"
                                    onSuccess={handleLoginWithGoogle}
                                    onFailure={failureLoginWithGoogle}
                                    cookiePolicy={'single_host_origin'}
                                />
                            </div>
                        </div> */}
                    </div>
                </div>        
            </div>
        </>
    )
}

function validationChar(char) {
    let charCode = char.charCodeAt(0);
    console.log(charCode);
    if (charCode === 34 || 
        charCode === 39 || 
        charCode === 38 || 
        charCode === 60 || 
        charCode === 62
    ) return false;

    else if (charCode < 33 ||
            127 <= charCode
    ) return false;

    else return true;

}

export default Login;
