import React, { useState } from "react";
// import GoogleLogin from "react-google-login";
import "../styles/login.css";
import API from "./api";
import { Form, Button } from "react-bootstrap";

const env = process.NODE_ENV;
// const googleClientId = env === "production" ?  process.env.REACT_APP_GOOGLE_CLIENT_ID : process.env.REACT_APP_GOOGLE_CLIENT_ID_DEV;
const LoginURI = env === "production" ? process.env.REACT_APP_API_LOGIN : "http://localhost:8000/login";
const SignUpURI = env === "production" ? process.env.REACT_APP_API_SIGNUP : "http://localhost:8000/signup";

function Login(props) {
    const [username, setUserName] = useState("");
    const [password, setPassword] = useState("")
    // const handleLoginWithGoogle = (response) => {
    //     const data = { token: response.tokenId };
    //     API.post("auth/google", {
    //         data
    //     })
    //     .then(response => {
    //         props.authenticate(true);
    //     })
    //     .catch(err => {
    //         console.log(err);
    //         props.authenticate(false);
    //     })
    // }

    // const failureLoginWithGoogle = (response) => {
    //     console.log(response);
    //     props.authenticate(false)
    // }

    const handleChange = (e) => {
        if (e.target.id === "username") {
            setUserName(e.target.value);
        }
        else {
            setPassword(e.target.value);
        }
    }



    return (
        <>
            <div className="d-flex justify-content-center align-items-center vh-100">
                <div className="login-container">
                    <div className="container py-4">
                        <div className="d-flex justify-content-center">
                            <h2 className="rem1">Welcome to My app</h2>
                        </div>
                        <Form action={SignUpURI} method="POST">
                            <Form.Group className="mb-3" controlId="formBasicText">
                                <Form.Label>User Name</Form.Label>
                                <Form.Control 
                                    id=""
                                    type="text" 
                                    placeholder="Username" 
                                    minLength="8" 
                                    maxLength="255"
                                    required
                                />
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formBasicPassword">
                                <Form.Label>Password</Form.Label>
                                <Form.Control
                                    id="password"
                                    minLength="8" 
                                    maxLength="255" 
                                    type="password" 
                                    placeholder="Password"
                                    value=""
                                    required
                                />
                            </Form.Group>
                            <div className="d-flex">
                                <Button variant="outline-secondary" type="submit">
                                    Sign Up
                                </Button>
                                {/* <Button className="mx-2 px-3" variant="outline-secondary" type="submit">
                                    Login
                                </Button> */}
                            </div>
                        </Form>                        
                        {/* <div className="d-flex justify-content-around pt-3">
                            <div className="login-with-google">
                                <GoogleLogin
                                    clientId={googleClientId}
                                    buttonText="Sign in with Google"
                                    onSuccess={handleLoginWithGoogle}
                                    onFailure={failureLoginWithGoogle}
                                    cookiePolicy={'single_host_origin'}
                                />
                            </div>
                        </div> */}
                    </div>
                </div>        
            </div>
        </>
    )
}

function validationChar(char) {
    let charCode = char.charCodeAt(0);
    console.log(`${char} : ${charCode}`);
    if (charCode === 34 || 
        charCode === 39 || 
        charCode === 38 || 
        charCode === 60 || 
        charCode === 62
    ) return false;

    else if (charCode < 33 ||
            127 <= charCode
    ) return false;

    else return true;

}

export default Login;
