import React, { useState } from "react";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Redirect
} from "react-router-dom";
import Board from "./pages/board";
import Login from "./pages/login";
import NotFoundPage from "./pages/notfound";
import { useCookies } from "react-cookie"; 


function App(props) {
    const [loggedIn, setAuthenticated] = useState(isAuthenticated());
    const [cookies, setCookie] = useCookies();
    console.log(cookies);
    return (
        <>
            <Router>
                <Switch>
                    <Route exact path="/board">
                        {loggedIn ? <Board tasklist={props.tasklist} userid={getIndiv()}/> :
                        <Redirect to="/" />}
                    </Route>
                    <Route exact path="/">
                        {loggedIn ? <Redirect to="/board" /> : 
                        <Login loggedIn={loggedIn} authenticate={setAuthenticated} setCookie={setCookie}/>}
                    </Route>
                    <Route>
                        <NotFoundPage />
                    </Route>
                </Switch>
            </Router>
        </>
        )
}

function isAuthenticated() {
    try {
        if (document.cookie.split(';').some((item) => item.trim().startsWith('indiv'))) {
            return true;
        }  
        else {
            return false;
        }
    } 
    catch {
        return false;
    }
}

function getIndiv() {
    if (isAuthenticated()) {
        return document.cookie
            .split('; ')
            .find(row => row.startsWith('indiv'))
            .split('=')[1];
    } 
    else {
        return "";
    }
}


export default App;
