import React, { useState } from "react";
import TaskCard from "./taskCard";
import EnterArea from "./enterArea";
import AddButton from "./addButton";
import TextareaAutosize from 'react-textarea-autosize';
import { VscClose } from "react-icons/vsc";
import API from "./api";
import cuttingStr from "./shaping";

function TaskList(props) {
    const [tasks, setTasks] = useState(props.task);
    const [inputMode, setEnable] = useState(false);
    const [taskName, setTaskName] = useState("");
    const [dynamisation, setDynamisation] = useState(false);
    const [tasklistID] = useState(props.id);
    const [listName, setListName] = useState(props.name);

    const keyPress = (e) => {
        if (e.which === 13) {
            setEnable(false)
            const data = {listid: tasklistID, listName: listName};
            API.put("tasklist", {
                data
            })
            .then(response => {
                setListName(cuttingStr(response.data.listName));
            })
            .catch(err => {
                // what should I do 
                window.location.reload();
            })
        }
    }

    const handleSubmit = (e, path, data) => {
        e.preventDefault();
        if (taskName.length === 0) return ;

        API.post(path, {
            data
        })
        .then(response => {
            const { taskID, taskName } = response.data;
            const newTask = { id: taskID, TaskName: cuttingStr(taskName) };
            setTasks([...tasks, newTask])
            setDynamisation(false)
            setTaskName("");
        })
        .catch(err => {
            // what should I do
            window.location.reload();
        })
    }

    const deleteTask = (path, data, id) => {
        API.delete(path, {
            data
        })
        .then(res => {
            // what should I do
        })
        .catch(err => {
            // what should I do
            window.location.reload();
        })
        const remainTasks = tasks.filter(task => id !== task.id);
        setTasks(remainTasks);
    }

    const taskList = tasks
        .map(task => (
            <TaskCard
                id={task.id}
                key={task.id}
                name={task.TaskName}
                deleteTask={deleteTask}
            />
        ))    

    const enterTaskName = 
        <EnterArea 
            handleSubmit={(e) => handleSubmit(e, "task", {taskName: taskName, tasklistID: tasklistID})}
            wrap="enter-task-name m-3"
            handleChange={(e) => setTaskName(e.target.value)}
            text="Add Task"
            placeholder="Enter task name..."
            cancel={(e) => {
                e.preventDefault()
                setDynamisation(false)
            }}
        />

    const addTaskBtn = 
        <AddButton 
            addElement={() => setDynamisation(true)}
            text="Add a Card"
        />

    const taskListName =  
            <>
                <h5 className="listName" onClick={() => setEnable(true)}>{listName}</h5>
                <div className="trash-box-icon" 
                    onClick={() => props.deletelist( 'tasklist', { listid: props.id }, props.id )}
                >
                    <VscClose/>
                </div>
            </>

    const textArea =
                <TextareaAutosize 
                    className="section-title w-100" 
                    onKeyPress={(e) => keyPress(e)} 
                    onChange={(e) => setListName(e.target.value)}
                    autoFocus={true}
                />

    return  (
            <div className="vh-100">
                <div className="mx-2 list-wrapper">
                    <div className="mx-3">
                        <div className="pt-3 sectionName-wrap">
                            {inputMode ? textArea : taskListName}
                        </div>
                    </div>
                    <div className="task-cards">
                        {taskList}
                    </div>
                        {!dynamisation ? addTaskBtn : enterTaskName}
                </div>
            </div>
        )
}


export default TaskList;
